import React from 'react';
import styled from 'styled-components';

const Operations = ({ gameOperations, handleOperationsChange }) => {
  return (
    <>
      <OperationsTitle>Operations:</OperationsTitle>
      <OperationsLabel htmlFor="addition">+</OperationsLabel>
      <OperationsSetting
        type="checkbox"
        id="addition"
        name="operations"
        value="addition"
        checked={gameOperations.includes('addition')}
        onChange={handleOperationsChange}
      />
      <OperationsLabel htmlFor="subtraction">-</OperationsLabel>
      <OperationsSetting
        type="checkbox"
        id="subtraction"
        name="operations"
        value="subtraction"
        onChange={handleOperationsChange}
      />
      <OperationsLabel htmlFor="multiplication">x</OperationsLabel>
      <OperationsSetting
        type="checkbox"
        id="multiplication"
        name="operations"
        value="multiplication"
        onChange={handleOperationsChange}
      />
    </>
  );
};
const OperationsLabel = styled.label`
  padding-right: 0px;
`;
const OperationsTitle = styled.p`
  padding-right: 5px;
`;
const OperationsSetting = styled.input`
  margin-right: 5px;
`;
export default Operations;
