import React, { useState, useEffect } from "react";
import styled from "styled-components";
import TodoContent from "./TodoContent";

const TodoItem = ({ value, itemId, deleteItem, editing, startEdit, confirmEdit }) => {
  const iconCheck = "far fa-check-circle";
  const iconDelete = "far fa-times-circle";
  const iconEdit = "far fa-edit";

  const handleDelete = (e) => {
    e.preventDefault();
    deleteItem(itemId);
  };

  const handleEdit = (e) => {
    e.preventDefault();
    startEdit(itemId);
    setTextContent(value);
  };

  const [textContent, setTextContent] = useState("");

  useEffect(() => {
    setTextContent(value);
  }, [value]);

  const handleEditChange = (e) => {
    setTextContent(e.currentTarget.value);
  };
  const handleEditSubmit = (e) => {
    confirmEdit(itemId, textContent);
  };

  return (
    <Wrapper>
      <Content>
        <TodoContent value={value} handleChange={handleEditChange} textContent={textContent} editing={editing} />
      </Content>
      <ButtonWrapper>
        <IconButton onClick={handleEdit}>
          <Icon className={editing ? iconDelete : iconEdit}></Icon>
        </IconButton>
        <EditConfirm editing={editing} onClick={handleEditSubmit}>
          <i className={iconCheck}></i>
        </EditConfirm>
        <Delete editing={editing} onClick={handleDelete}>
          <i className={iconDelete}></i>
        </Delete>
        <Complete editing={editing}>
          <i className={iconCheck}></i>
        </Complete>
      </ButtonWrapper>
    </Wrapper>
  );
};

const Wrapper = styled.li`
  width: 100%;
  /* background: ; */
  /* min-height: 100px; */
  border: 2px solid var(--highlight-screen);
  background-color: var(--color-screen);
  padding: 10px 10px 5px 10px;
  border-radius: 8px;
  box-shadow: 0px 0px 10px 4px var(--highlight-alternative-border-light);
  margin-bottom: 10px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  /* align-items: center; */
`;
const Content = styled.article`
  text-shadow: var(--shadow-text);

  /* width: 75%; */
  font-size: 18px;
  padding-bottom: 5px;
`;
const ButtonWrapper = styled.div`
  display: flex;
  align-items: center;
  border-top: 2px solid var(--highlight-screen);
  padding-top: 5px;
`;

const IconButton = styled.button`
  background: none;
  border: none;
  font-size: 24px;
  text-shadow: var(--shadow-text);
  padding: 0px 5px;
  /* margin: 0px 5px; */
  /* border: 2px solid black; */
`;

const EditConfirm = styled(IconButton)`
  display: ${(props) => (props.editing ? "visible" : "none")};
`;
const Complete = styled(IconButton)`
  margin-left: auto;
  display: ${(props) => (props.editing ? "none" : "visible")};
`;
const Delete = styled(IconButton)`
  display: ${(props) => (props.editing ? "none" : "visible")};
`;

const Icon = styled.i`
  transform: translateY(-1px);
`;

export default TodoItem;
