import React, { useState, useEffect } from "react";
import styled from "styled-components";
import TodoItem from "./TodoItem";
// import List from "./List";
import ScreenIndent from "../ScreenIndent";
import CoolScreen from "../CoolScreen";
import TodoForm from "./TodoForm";

const TodoList = () => {
  const todoTag = "todoList";
  const [list, setList] = useState([]);

  useEffect(() => {
    // const arr = ["first item", "second item"];
    // window.localStorage.setItem(todoTag, JSON.stringify(arr));
    // window.localStorage.clear();
    const windowStorage = JSON.parse(window.localStorage.getItem(todoTag));
    // console.log(windowStorage);
    if (windowStorage) {
      setList(windowStorage);
    }
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    // console.log(e.target[0].value);
    const todoVal = e.target[0].value;
    e.target[0].value = "";
    setList(() => {
      return [...list, { id: list.length, value: todoVal }];
    });
  };

  const deleteItem = (id) => {
    const filteredList = list.filter((e) => e.id !== id);
    setList(() => filteredList);
  };

  useEffect(() => {
    window.localStorage.setItem(todoTag, JSON.stringify(list));
    // console.log(window.localStorage);
  }, [list]);

  return (
    <Wrapper>
      <ScreenIndent long>
        <CoolScreen sideScreen>
          <TodoForm handleSubmit={handleSubmit} />
          <ListWrapper>
            {/* <TodoItem value="Finish project wireframe" />
            <TodoItem />
            <TodoItem /> */}
            {list.map((e) => {
              return <TodoItem itemId={e.id} key={e + e.id} value={e.value} deleteItem={deleteItem} />;
            })}
          </ListWrapper>
        </CoolScreen>
      </ScreenIndent>
    </Wrapper>
  );
};

const Wrapper = styled.article`
  width: 100%;
  flex: 1;
  height: 100%;
  /* border: 10px solid var(--shadow-main-border-dark);
  border-right: 6px solid var(--shadow-main-border-light);
  border-bottom: 6px solid var(--shadow-main-border-light);
  border-radius: 20px;
  box-shadow: var(--inset-shadow);
  background-color: var(--shadow-main); */
  /* margin-bottom: 20px; */
  position: relative;
`;
const ListWrapper = styled.ul`
  width: 100%;
  list-style: none;
  padding: 0px 10px;
`;

export default TodoList;
