import React from "react";
import styled from "styled-components";
import Extensions from "./Extensions";

const Bookmark = ({ title, baseURL, extensions }) => {
  if (extensions) {
    return (
      <Wrapper>
        <BaseAnchor href={baseURL}>
          <Title>{title}</Title>
          <AnchorIcon className="far fa-arrow-alt-circle-right"></AnchorIcon>
        </BaseAnchor>
        <Extensions extensions={extensions} baseURL={baseURL} />
      </Wrapper>
    );
  }
  return (
    <Wrapper>
      <BaseAnchor href={baseURL}>
        <Title>{title}</Title>
        <AnchorIcon className="far fa-arrow-alt-circle-right"></AnchorIcon>
      </BaseAnchor>
    </Wrapper>
  );
};

const Wrapper = styled.article`
  width: 100%;

  border: 2px solid var(--highlight-screen);
  background-color: var(--color-screen);
  /* padding: 10px 10px 5px 10px; */
  border-radius: 8px;
  box-shadow: 0px 0px 10px 4px var(--highlight-alternative-border-light);
  margin-bottom: 10px;
`;

const Anchor = styled.a`
  text-decoration: none;
`;
const BaseAnchor = styled(Anchor)`
  padding: 10px 5px;
  display: flex;
  justify-content: space-between;
`;

const Title = styled.h2`
  /* border: 2px solid red; */
  transform: translateY(2px);
  width: 100%;
  font-size: 18px;
  text-shadow: var(--shadow-text);
  font-weight: 600;
  letter-spacing: 2px;
  color: var(--color-text);
  ${Anchor}:hover & {
    transition: color, text-shadow;
    transition-duration: 0.3s;
    transition-timing-function: ease;
    color: var(--hover-main-text);
    text-shadow: var(--hover-main-text-shadow);
  }
`;
const ExtensionsWrapper = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: column;
`;
const ExtensionsList = styled.div`
  height: auto;
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  padding: 5px 0px;
  border-top: 2px solid var(--highlight-screen);
  border-bottom: 2px solid var(--highlight-screen);
`;
const ExtensionAnchor = styled(Anchor)`
  margin: 5px 5px;
  padding: 5px 6px 5px 8px;
  border-radius: 8px;
  background-color: var(--color-screen);
  border: 2px solid var(--highlight-screen);
  transition: border-color, box-shadow, background-color;
  transition-duration: 0.5s;
  transition-timing-function: ease;
  &:hover {
    /* text-shadow: var(--hover-main-shadow); */
    border-color: var(--hover-main);
    background-color: var(--hover-main-bg);
    box-shadow: 0px 0px 10px 1px var(--hover-main);

    transition: border-color, box-shadow, background-color;
    transition-duration: 0.2s;
    transition-timing-function: ease;
  }
`;
const Extension = styled.p`
  font-size: 16px;
  text-shadow: var(--shadow-text);
  font-weight: 600;
  letter-spacing: 2px;
  color: var(--color-text);
  /* border: 2px solid red; */
`;
const ExtendBtn = styled.button`
  background: none;
  border: none;
  width: 100%;
  /* border-top: 2px solid var(--highlight-screen); */

  cursor: pointer;
`;
const Icon = styled.i`
  /* border: 2px solid red; */
  font-size: 26px;
  color: var(--color-text);
  text-shadow: var(--shadow-text);
  transition: color, text-shadow;
  transition-duration: 0.3s;
  transition-timing-function: ease;
  cursor: pointer;
`;
const AnchorIcon = styled(Icon)`
  ${Anchor}:hover & {
    transition: color, text-shadow;
    transition-duration: 0.3s;
    transition-timing-function: ease;
    color: var(--hover-main-text);
    text-shadow: var(--hover-main-text-shadow);
  }
`;
const ExtendIcon = styled(Icon)`
  ${ExtendBtn}:hover & {
    transition: color, text-shadow;
    transition-duration: 0.3s;
    transition-timing-function: ease;
    color: var(--hover-main-text);
    text-shadow: var(--hover-main-text-shadow);
  }
`;

export default Bookmark;
