import React from 'react';
import styled from 'styled-components';
import Btn from '../../../Btn';
import Score from './Score';

const CompletedGame = ({ correctQuestions, incorrectQuestions }) => {
  const totalArr = [...correctQuestions, ...incorrectQuestions];

  const getOperationTotal = (arr, operation) => {
    const operationArr = arr.filter((q) => q.operation === operation);
    console.log(operationArr);
    // const operationArr = arr.reduce((prev, current) => {
    //   return current.operation === operation && [...prev, current];
    // }, []);
    // console.log(operationArr, 'test');
    // return operationArr.length;
  };
  console.log(getOperationTotal(incorrectQuestions, 'addition'));

  return (
    <Wrapper>
      <Title>Game Over</Title>
      <Subtitle>Score</Subtitle>
      <Score valOne={correctQuestions.length} valTwo={totalArr.length} operation="Total" />
      <Score>Multiplication: 6 / 6</Score>
      <Score>Addition: 6 / 7</Score>
      <Score>Subtraction: 7 / 7</Score>
      <BtnWrapper>
        <StyledBtn>Replay</StyledBtn>
        <StyledBtn>Exit</StyledBtn>
      </BtnWrapper>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  background-color: var(--color-screen);
  border: 2px solid var(--highlight-screen);
  box-shadow: var(--highlight-screen-shadow);
  border-radius: 8px;
  min-width: 600px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding-bottom: 10px;
`;
const Title = styled.h1`
  font-size: 3rem;
  color: var(--hover-danger);
  text-shadow: var(--hover-danger-text-shadow);
`;
const Subtitle = styled.h2`
  font-size: 2rem;
  text-decoration: underline;
`;

const BtnWrapper = styled.div`
  display: flex;
  width: 100%;
  justify-content: center;
  padding-top: 10px;
`;
const StyledBtn = styled(Btn)`
  padding: 5px 10px;
  font-size: 1.2rem;
  margin: 0px 5px;
`;

export default CompletedGame;
