import React from "react";
import styled from "styled-components";

const Bookmark = ({ title, baseURL, extensions }) => {
  return (
    <Wrapper>
      <Anchor>
        <Title>{title}</Title>
      </Anchor>
      <Extension>
        <ExtendBtn>
          <Icon className="fas fa-caret-down"></Icon>
        </ExtendBtn>
      </Extension>
    </Wrapper>
  );
};

const Wrapper = styled.article`
  width: 100%;

  border: 2px solid var(--highlight-screen);
  background-color: var(--color-screen);
  /* padding: 10px 10px 5px 10px; */
  border-radius: 8px;
  box-shadow: 0px 0px 10px 4px var(--highlight-alternative-border-light);
  margin-bottom: 10px;
`;
const Title = styled.h2`
  padding: 10px 5px;
  width: 100%;
  font-size: 18px;
  text-shadow: var(--shadow-text);
  font-weight: 600;
  letter-spacing: 2px;
  color: var(--color-text);
  border-bottom: 2px solid var(--highlight-screen);
`;
const Anchor = styled.a``;
const Extension = styled.div`
  display: flex;
  justify-content: center;
`;

const Icon = styled.i`
  font-size: 28px;
  color: var(--color-text);
  text-shadow: var(--shadow-text);
  transition: color, text-shadow, transform;
  transition-duration: 0.3s;
  transition-timing-function: ease;
  cursor: pointer;
  &:hover {
  }
`;
const ExtendBtn = styled.button`
  background: none;
  border: none;
  width: 100%;
  border: 2px solid red;
  cursor: pointer;

  ${Icon} &:hover {
    transition: color, text-shadow, transform;
    transition-duration: 0.3s;
    transition-timing-function: ease;
    color: var(--hover-main-text);
    text-shadow: var(--hover-main-text-shadow);
    transform: translate(-1px, -1px);
  }
`;

export default Bookmark;
