import React, { useState } from "react";
import styled from "styled-components";

const APOD = ({ data, loading, error }) => {
  const [isExtended, setIsExtended] = useState(false);

  const handleClick = () => {
    setIsExtended(() => !isExtended);
  };

  return (
    <Wrapper>
      <Title>{data.title}</Title>
      <Subtitle>{data.copyright}</Subtitle>
      {/* <Image src={data.url} /> */}
      <ExtensionWrapper>
        <Description>
          {data.explanation} asd as das dasd as dasd as das das das dasd asd asd as dasdasd asd asd as ds a Lorem ipsum
          dolor sit amet consectetur adipisicing elit. Quasi quo porro repellendus velit? Ad, quia? Laudantium pariatur
          corrupti iure neque, sint doloribus quia laborum est, ducimus facere saepe, beatae deserunt. Lorem ipsum dolor
          sit amet consectetur, adipisicing elit. Optio omnis, sunt ipsum ea neque, debitis magni quisquam, quas in
          minima accusamus nesciunt iure officiis voluptas. Blanditiis tempora voluptates unde quasi? Veniam ipsa quia
          quam fuga atque expedita nam doloribus id illum sint enim dolorem doloremque dolores omnis nostrum qui
          corporis natus numquam at, officiis deleniti iure voluptatem. Corporis, sint natus? Esse dolorum quisquam nisi
          tenetur delectus, eaque eos reprehenderit! Eum magni et voluptas vero facilis dicta placeat quidem quam
          voluptatibus ab molestias harum, fuga aliquam amet optio neque illum molestiae! Aperiam, excepturi quaerat
          quae amet ut maiores libero iusto commodi fuga similique ducimus, nesciunt possimus placeat nihil maxime
          voluptas quia ex debitis vel! Nam ut modi repellat officiis praesentium quis! Consectetur provident aperiam
          rerum expedita corrupti officiis, enim quos magni aliquam quaerat, vitae, vel aliquid voluptates odio cum
          vero. Laborum quas consequuntur reiciendis magni sed placeat voluptates sequi delectus fugiat. mquam at,
          officiis deleniti iure voluptatem. Corporis, sint natus? Esse dolorum quisquam nisi tenetur delectus, eaque
          eos reprehenderit! Eum magni et voluptas vero facilis dicta placeat quidem quam voluptatibus ab molestias
          harum, fuga aliquam amet optio neque illum molestiae! Aperiam, excepturi quaerat quae amet ut maiores libero
          iusto commodi fuga similique ducimus, nesciunt possimus placeat nihil maxime voluptas quia ex debitis vel! Nam
          ut modi repellat officiis praesentium quis! Consectetur provident aperiam rerum expedita corrupti officiis,
          enim quos magni aliquam quaerat, vitae, vel aliquid voluptates odio cum vero. Laborum quas consequuntur
          reiciendis magni sed placeat voluptates sequi delectus fugiat.
        </Description>
        <ExtensionBtn onClick={handleClick}>
          Description<ExtensionIcon className={isExtended ? "fas fa-caret-up" : "fas fa-caret-down"}></ExtensionIcon>
        </ExtensionBtn>
      </ExtensionWrapper>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  width: 100%;
  height: 100%;
  padding: 10px 20px 5px 20px;
  border-radius: 8px;
  background-color: var(--color-screen);
  border: 2px solid var(--highlight-screen);
  box-shadow: 0px 0px 10px 4px var(--highlight-alternative-border-light);
  /* overflow-y: auto; */
`;
const Title = styled.h2`
  text-shadow: var(--shadow-text);
  letter-spacing: 2px;
  color: var(--color-text);
  padding-bottom: 10px;
  text-align: center;
  font-size: 24px;
`;
const Subtitle = styled.h3`
  text-shadow: var(--shadow-text);
  letter-spacing: 2px;
  color: var(--color-text);
  padding-bottom: 10px;
  text-align: center;
  font-size: 18px;
`;
const Image = styled.img`
  max-width: 100%;
  height: auto;
  display: block;
  border-radius: 8px;
  border: 2px solid var(--highlight-screen);
  box-shadow: 0px 0px 10px 4px var(--highlight-alternative-border-light);
`;
const Description = styled.div`
  text-shadow: var(--shadow-text);
  letter-spacing: 2px;
  color: var(--color-text);
  font-family: var(--font-family-main);
  font-size: 16px;
  border: 2px solid black;
  /* height: 200px; */
`;
const ExtensionWrapper = styled.div`
  width: 100%;
  /* height: 100%; */
  /* border: 2px solid red; */
  padding-top: 5px;
  display: flex;
  flex-direction: column;
`;
const ExtensionBtn = styled.button`
  border: none;
  background: none;
  display: flex;
  flex-direction: column;
  align-items: center;
  text-shadow: var(--shadow-text);
  letter-spacing: 2px;
  color: var(--color-text);
  font-family: var(--font-family-main);
  font-size: 16px;
`;
const ExtensionIcon = styled.i`
  font-size: 30px;
`;

export default APOD;
