import { useState } from 'react';
import styled from 'styled-components';
import OperationIcon from './OperationIcon';
import EqualsIcon from '../../../../icons/Equals';
import Btn from '../../../Btn';

const Question = ({ valOne, valTwo, operation }) => {
  const [answer, setAnswer] = useState('');
  const [isAnswered, setIsAnswered] = useState(false);
  const [isRight, setIsRight] = useState();

  const handleChange = (e) => {
    setAnswer(() => e.target.value);
  };
  const checkAnswer = (e) => {
    e.preventDefault();
    if (operation === 'addition') {
      const trueAnswer = parseInt(valOne) + parseInt(valTwo);
      setIsRight(() => trueAnswer === parseInt(answer));
      setIsAnswered(() => true);
    } else if (operation === 'subtraction') {
      const trueAnswer = parseInt(valOne) - parseInt(valTwo);
      setIsRight(() => trueAnswer === parseInt(answer));
      setIsAnswered(() => true);
    } else {
      const trueAnswer = parseInt(valOne) * parseInt(valTwo);
      setIsRight(() => trueAnswer === parseInt(answer));
      setIsAnswered(() => true);
    }
  };
  if (!isAnswered) {
    return (
      <Wrapper onSubmit={checkAnswer}>
        <Container>
          {valOne} <OperationIcon operation={operation} /> {valTwo} <StyledEqualsIcon />
          <AnswerInput type="number" value={answer} onChange={handleChange} />
        </Container>
        <SubmitBtn type="submit">Submit</SubmitBtn>
      </Wrapper>
    );
  } else if (isRight) {
    <Wrapper onSubmit={checkAnswer}>
      <Container>
        {valOne} <OperationIcon operation={operation} /> {valTwo} <StyledEqualsIcon />
        <span>{answer}</span>
      </Container>
      <SubmitBtn type="submit">Submit</SubmitBtn>
    </Wrapper>;
  }
};

const Wrapper = styled.form`
  font-size: 1.2rem;
  background-color: var(--color-screen);
  border: 2px solid var(--highlight-screen);
  box-shadow: var(--highlight-screen-shadow);
  border-radius: 8px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-width: 300px;
  padding: 20px;
  margin: 10px;
`;

const StyledEqualsIcon = styled(EqualsIcon)`
  height: 16px;
  width: 16px;
  margin: 0px 4px;
`;
const AnswerInput = styled.input`
  font-size: inherit;
  font-family: inherit;
  background-color: var(--color-screen);
  border: 2px solid var(--highlight-screen);
  box-shadow: var(--highlight-screen-shadow);
  border-radius: 8px;
  width: 100px;
  padding: 5px;
  margin: 0;
  color: var(--color-text);
  text-shadow: var(--shadow-text);
  -moz-appearance: none;
  &::-webkit-outer-spin-button,
  &::-webkit-inner-spin-button {
    -webkit-appearance: none;
    margin: 0;
  }
`;
const Container = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 10px;
`;
const SubmitBtn = styled(Btn)`
  padding: 5px 10px;
  font-size: 1rem;
`;

export default Question;
