import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import TodoItem from './TodoItem';
import TodoForm from './TodoForm';

const TodoList = () => {
  const todoTag = 'todoList';

  const [list, setList] = useState([]);

  useEffect(() => {
    const windowStorage = JSON.parse(window.localStorage.getItem(todoTag));
    if (windowStorage) {
      setList(windowStorage);
    }
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(e);
    const todoVal = e.target[0].value;
    if (!todoVal) {
      return;
    }

    e.target[0].value = '';
    setList(() => {
      return [...list, { id: list.length, value: todoVal, editing: false }];
    });
  };

  const deleteItem = (id) => {
    const filteredList = list.filter((e) => e.id !== id);
    setList(() => filteredList);
  };

  const startEdit = (id) => {
    const newList = list.map((e) => {
      if (e.id === id) {
        e.editing = !e.editing;
        return e;
      }
      return e;
    });
    setList(() => newList);
  };

  const confirmEdit = (id, newText) => {
    const newList = list.map((e) => {
      if (e.id === id) {
        e.value = newText;
        e.editing = !e.editing;
        return e;
      }
      return e;
    });
    setList(() => newList);
  };

  useEffect(() => {
    window.localStorage.setItem(todoTag, JSON.stringify(list));
  }, [list]);

  return (
    <Wrapper>
      <TodoForm handleSubmit={handleSubmit} />
      <ListWrapper>
        {list.map((e) => {
          return (
            <TodoItem
              itemId={e.id}
              key={e + e.id}
              value={e.value}
              deleteItem={deleteItem}
              startEdit={startEdit}
              confirmEdit={confirmEdit}
              editing={e.editing}
            />
          );
        })}
      </ListWrapper>
    </Wrapper>
  );
};

const Wrapper = styled.section`
  width: 100%;
  height: 100%;
  position: relative;
  overflow: auto;
`;

const ListWrapper = styled.ul`
  width: 100%;
  list-style: none;
  padding: 0px 10px;
  overflow: auto;
`;

export default TodoList;
