//   border-bottom: 8px solid var(--shadow-main-border-light);
//   border-radius: 20px;
//   background-color: var(--shadow-main);
//   box-shadow: var(--inset-shadow);

//   width: 100%;
//   height: 100%;
// `;

export default Screen;
