import React, { useState } from 'react';
import styled from 'styled-components';
import ScreenIndent from '../../ScreenIndent';
import CoolScreen from '../../CoolScreen';
import NavButton from './NavButton';
import SnippetsIcon from '../../../icons/Snippets';
import BugIcon from '../../../icons/Bug';
import HomeIcon from '../../../icons/Home';
import MathIcon from '../../../icons/Math';

const NavScreen = () => {
  const [currentPath, setCurrentPath] = useState(window.location.pathname);
  const handleNav = (e) => {
    const path = e.currentTarget.attributes.path.value;

    setCurrentPath(() => path);
  };
  return (
    <Wrapper>
      <NavIndent long>
        <CoolScreen sideScreen style={{ paddingTop: '0px' }}>
          <NavList>
            <NavButton path="/">
              <HomeIcon />
              Home
            </NavButton>
            <NavButton path="/snippets">
              <SnippetsIcon />
              Snippets
            </NavButton>
            <NavButton path="/bugs">
              <BugIcon />
              Bugs
            </NavButton>
            <NavButton path="/math">
              <MathIcon />
              Math
            </NavButton>
          </NavList>
        </CoolScreen>
      </NavIndent>
    </Wrapper>
  );
};

const Wrapper = styled.nav`
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0px 30px 30px;
`;

const NavIndent = styled(ScreenIndent)`
  width: 100%;
  height: 100%;
  /* border: 2px solid red; */
`;
const NavList = styled.ul`
  display: flex;
  justify-content: space-around;
  align-items: center;
  list-style: none;
  height: 100%;
  width: 100%;
`;

export default NavScreen;
