import React from 'react';
import styled from 'styled-components';
import PageTitle from '../PageTitle';

const Math = () => {
  return (
    <Wrapper>
      <PageTitle>Math Blitz</PageTitle>
      <RulesWrapper>
        <RulesTitle>How to play</RulesTitle>
        <RulesText>
          Lorem ipsum dolor sit amet, consectetur adipisicing elit. Consequatur deserunt sequi quo expedita inventore,
          impedit explicabo delectus minus quasi aspernatur laborum labore maiores ut quisquam fuga natus perspiciatis,
          nam molestiae.
        </RulesText>
      </RulesWrapper>
      <SettingsWrapper>
        <SettingsTitle>Settings</SettingsTitle>
        <SettingsForm>
          <SettingsLabel>Time:</SettingsLabel>
          <TimeSetting></TimeSetting>
          <SettingsLabel>Operations:</SettingsLabel>
          <OperationsSetting type="radio"></OperationsSetting>
          <SettingsLabel>Difficulty:</SettingsLabel>
          <DifficultySetting></DifficultySetting>
          <BtnWrapper>
            <StartBtn>Start</StartBtn>
          </BtnWrapper>
        </SettingsForm>
      </SettingsWrapper>
    </Wrapper>
  );
};

const Wrapper = styled.section`
  width: 100%;
  /* border: 2px solid red; */
  display: flex;
  flex-direction: column;
  align-items: center;
`;
const RulesWrapper = styled.div`
  letter-spacing: 2px;
  color: var(--color-text);
  background-color: var(--color-screen);
  border: 2px solid var(--highlight-screen);
  box-shadow: 0px 0px 10px 4px var(--highlight-alternative-border-light);
  border-radius: 8px;
  margin-top: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 10px 30px;
  max-width: 500px;
`;
const RulesTitle = styled.h2`
  font-size: 1.8rem;
  text-shadow: var(--shadow-text);
  font-weight: 400;
  padding-bottom: 10px;
`;
const RulesText = styled.p`
  text-shadow: var(--shadow-text);
`;
const SettingsWrapper = styled.div`
  letter-spacing: 2px;
  color: var(--color-text);
  background-color: var(--color-screen);
  border: 2px solid var(--highlight-screen);
  box-shadow: 0px 0px 10px 4px var(--highlight-alternative-border-light);
  border-radius: 8px;
  margin-top: 20px;
  max-width: 500px;
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 500px;
  padding: 10px 30px;
`;
const SettingsTitle = styled.h2`
  font-size: 1.8rem;
  text-shadow: var(--shadow-text);
  font-weight: 400;
  padding-bottom: 10px;
`;
const SettingsForm = styled.form`
  width: 100%;
`;
const SettingsLabel = styled.label``;
const TimeSetting = styled.select``;
const OperationsSetting = styled.input``;
const DifficultySetting = styled.select``;
const BtnWrapper = styled.div`
  display: flex;
  justify-content: center;
  padding: 5px 0px;
`;
const StartBtn = styled.button``;

export default Math;
